import{_ as t,p as e,q as d,a1 as s}from"./framework-bf3e1922.js";const r={},i=s('<h1 id="roles-responsibilities" tabindex="-1"><a class="header-anchor" href="#roles-responsibilities" aria-hidden="true">#</a> Roles &amp; Responsibilities</h1><p>There are four groups primarily involved in cloud operations:</p><ul><li><strong>EACD-CloudOps</strong> represents AS&#39; interests, sets standards for AS infrastructure, creates shared IaC modules, and maintains the GitHub organization</li><li><strong>PIPS-CloudOps</strong> is responsible for Northwestern&#39;s entire AWS account infrastructure, the VPNs back to the datacenters, and maintains the Jenkins servers</li><li><strong>Lead Developers</strong> are responsible for their applications, application-specific infrastructure, and CI/CD pipelines</li><li><strong>Developers</strong> create &amp; maintain apps, app-specific infrastructure, and CI/CD pipelines</li></ul><h2 id="raci" tabindex="-1"><a class="header-anchor" href="#raci" aria-hidden="true">#</a> RACI</h2><p>Here is a responsibility assignment matrix that covers common tasks/functions.</p><ul><li><strong>R</strong>: Responsible, does the work</li><li><strong>A</strong>: Accountable, assigns/approves the work</li><li><strong>C</strong>: Consulted, subject matter expects who provide input</li><li><strong>I</strong>: Informed, kept up-to-date</li></ul><table><thead><tr><th><em>Function</em></th><th>EACD-CloudOps</th><th>PIPS-CloudOps</th><th>Lead Developers</th><th>Developers</th></tr></thead><tbody><tr><td>Submit requests for staff access to cloud resources</td><td></td><td></td><td>AR</td><td></td></tr><tr><td>Approve &amp; process requests for staff access to cloud resources</td><td>A</td><td>R</td><td>I</td><td></td></tr><tr><td>Jenkins server (EC2 &amp; Jenkins service) maintenance</td><td>C</td><td>A</td><td>I</td><td>I</td></tr><tr><td>Jenkins plugin maintenance</td><td>A</td><td>C</td><td>I</td><td>I</td></tr><tr><td>Creating Jenkins jobs (unconfigured) &amp; setting permissions</td><td>AR</td><td></td><td>C</td><td>I</td></tr><tr><td>Writing &amp; maintaining Jenkins pipelines</td><td>C</td><td></td><td>A</td><td>R</td></tr><tr><td>VPC &amp; VPN set-up/maintenance</td><td>C</td><td>AR</td><td>C</td><td>I</td></tr><tr><td>Assigning subnets for applications</td><td>A</td><td></td><td>I</td><td>I</td></tr><tr><td>Maintaining shared AWS resources (e.g. ALB, SES)</td><td>A</td><td></td><td>I</td><td>I</td></tr><tr><td>Creating &amp; maintaning application IaC</td><td>C</td><td></td><td>A</td><td>R</td></tr><tr><td>Reviewing pull requests affecting IaC</td><td>C</td><td></td><td>AR</td><td>I</td></tr><tr><td>Reviewing pull requests for applications</td><td></td><td></td><td>AR</td><td>I</td></tr><tr><td>Application deployments</td><td></td><td></td><td>A</td><td>R</td></tr></tbody></table>',7),n=[i];function a(o,l){return e(),d("div",null,n)}const c=t(r,[["render",a],["__file","roles-responsibilities.html.vue"]]);export{c as default};
